+.pb-layout-admin .pb-side {
+  top: 60px;
+  width: 200px;
+  overflow-x: hidden;
+}
+.pb-side-scroll {
+  position: relative;
+  width: 220px;
+  height: 100%;
+  overflow-x: hidden;
+}
+.pb-nav-tree .pb-nav-itemed:after {
+  position: absolute;
+  left: 0;
+  top: 0;
+  width: 0;
+  height: 5px;
+  background-color: #5fb878;
+  transition: all 0.2s;
+  -webkit-transition: all 0.2s;
+}
+.pb-nav-tree {
+  width: 200px;
+  padding: 0;
+}
+.pb-nav-tree .pb-nav-item {
+  display: block;
+  width: 100%;
+  line-height: 45px;
+}
+.pb-nav-tree .pb-nav-item a {
+  position: relative;
+  height: 45px;
+  line-height: 45px;
+  text-overflow: ellipsis;
+  overflow: hidden;
+  white-space: nowrap;
+}
+.pb-nav-tree .pb-nav-item a:hover {
+  background-color: #4e5465;
+}
+.pb-nav-tree .pb-nav-child {
+  position: relative;
+  z-index: 0;
+  top: 0;
+  border: none;
+  box-shadow: none;
+}
+.pb-nav-tree .pb-nav-child a {
+  height: 40px;
+  line-height: 40px;
+  color: #fff;
+  color: rgba(255, 255, 255, 0.7);
+}
+.pb-nav-tree .pb-nav-child,
+.pb-nav-tree .pb-nav-child a:hover {
+  background: 0 0;
+  color: #fff;
+}
+.pb-nav-tree .pb-nav-more {
+  right: 10px;
+}
+.pb-nav-itemed > .pb-nav-child {
+  display: block;
+  padding: 0;
+  background-color: rgba(0, 0, 0, 0.3) !important;
+}
+.pb-nav-tree .pb-nav-item > a .pb-nav-more {
+  padding: 5px 0;
+}
+.pb-nav .pb-nav-mored,
+.pb-nav-itemed > a .pb-nav-more {
+  transform: rotate(180deg);
+}
+.pb-nav-itemed>a{
+  color: #fff !important;
+}

